body.raw:
{
  "query": "query GetAllTasks { tasks { _id title description status priority tags assignee dueDate checklist { text completed } createdAt updatedAt } }"
}

{
  "query": "query GetTask($id: ID!) { task(id: $id) { _id title description status priority tags assignee dueDate checklist { text completed } createdAt updatedAt } }",
  "variables": {
    "id": "your_task_id_here"
  }
}

{
  "query": "query GetTasksByStatus($status: String!) { tasksByStatus(status: $status) { _id title description status priority assignee checklist { text completed } } }",
  "variables": {
    "status": "todo"
  }
}

{
  "query": "mutation CreateTask($input: TaskInput!) { createTask(input: $input) { _id title description status priority tags assignee dueDate checklist { text completed } } }",
  "variables": {
    "input": {
      "title": "Нова задача",
      "description": "Опис нової задачі",
      "status": "todo",
      "priority": "high",
      "tags": ["важливе", "термінове"],
      "assignee": "Іван",
      "dueDate": "2024-03-25",
      "checklist": [
        {
          "text": "Підзадача 1",
          "completed": false
        }
      ]
    }
  }
}

{
  "query": "mutation UpdateTask($id: ID!, $input: TaskInput!) { updateTask(id: $id, input: $input) { _id title description status priority tags assignee dueDate checklist { text completed } updatedAt } }",
  "variables": {
    "id": "your_task_id_here",
    "input": {
      "title": "Оновлена задача",
      "description": "Оновлений опис задачі",
      "status": "in-progress",
      "priority": "medium",
      "tags": ["оновлено"],
      "assignee": "Петро",
      "dueDate": "2024-03-30",
      "checklist": [
        {
          "text": "Оновлена підзадача",
          "completed": true
        }
      ]
    }
  }
}

{
  "query": "mutation UpdateTaskStatus($id: ID!, $status: String!) { updateTaskStatus(id: $id, status: $status) { _id title status updatedAt } }",
  "variables": {
    "id": "your_task_id_here",
    "status": "done"
  }
}

{
  "query": "query GetTasksWithFilters($status: String, $priority: String) { tasks { _id title status priority assignee dueDate checklist { text completed } } }",
  "variables": {
    "status": "in-progress",
    "priority": "high"
  }
}
